'use strict';

function Scenario(loadedModel, syncId) {
    this.id                = loadedModel.generateId();
    this.syncId            = syncId;

    this.name              = 'New scenario';
    this.data              = {};

    this.measurement       = 'Week';
    this.measurementAmount = 1;
    this.maxIterations     = 4;
    this.timeStepN         = 0;
}

module.exports = Scenario;
