'use strict';

var Immutable = null;

module.exports = function createLink(generateId, source, destination, type) {
    return {
        id:            generateId(),
        node1:         source,
        node2:         destination,
        coefficient:   1,
        type:          type || 'fullchannel',
        timelag:       0,
        threshold:     0,
        width:         8,
        bidirectional: false,
        bidirectionalTimelag: 0,

        objectId:      'link'
    };
};
